return (() => {
const result = [];

const data = items[0].json.output;

// 1. Total Leads
result.push({
"Metric Type": "Summary",
"Label": "Total Leads",
"Value": data.totalLeads
});

// 2. Total Pipeline Value
result.push({
"Metric Type": "Summary",
"Label": "Total Value",
"Value": data.totalPipelineValue
});

// 3. Status Breakdown
for (const status in data.statusBreakdown) {
result.push({
"Metric Type": "Status Count",
"Label": status,
"Value": data.statusBreakdown[status]
});
}

// 4. Stage Breakdown
for (const stage in data.stageBreakdown) {
result.push({
"Metric Type": "Stage Count",
"Label": stage,
"Value": data.stageBreakdown[stage]
});
}

// 5. Top Reps
for (const rep of data.topReps) {
result.push({
"Metric Type": "Top Reps",
"Label": rep.rep,
"Value": rep.totalValue
});
}

return result.map(r => ({ json: r }));
})();
